import { Component, EventEmitter, Output } from '@angular/core';

@Component({
  selector: 'app-login-form',
  templateUrl: './login-form.component.html',
  styleUrls: ['./login-form.component.css']
})
export class LoginFormComponent {
  @Output() onSubmitLoginEvent=new EventEmitter();
  login:string="";
  password:string="";
active: any;
onLoginTab: any;

  onSubmitLogin():void{
    this.onSubmitLoginEvent.emit({
      "login":this.login,
      "password":this.password
    })
  }

}
<div class="row d-flex justify-content-center">
    <div class="col-4">

        <ul class="nav nav-pills nav justified mb-3" id="ex-1" role="tablist">
            <li class="nav-item" role="presentation">
                <button [ngClass]="active=='login'? 'nav-link-active':'nav-link'" id="tab-login"
                (onClick)="onLoginTab()">Log In</button>
            </li>
            <li class="nav-item" role="presentation">
                <button [ngClass]="active=='register'? 'nav-link-active':'nav-link'" id="tab-register"
                (onClick)="onLoginTab()">Register</button>
            </li>
        </ul>
        <form (ngSubmit)="onSubmitLogin()" #loginForm="ngForm">
            <div class="form-outline mb-4">
                <label class="form-label" for="loginName">User Name :</label>
                <input class="form-control" type="login" id="loginName" name="login" [(ngModel)]="login">
            </div>

            <div class="form-outline mb-4">
                <label class="form-label" for="loginPassword">Password :</label>
                <input class="form-control" type="password" id="loginPassword" name="login" [(ngModel)]="password">
            </div>

            <button type="submit" class="btn btn-primary btn-block mb-4">Sing in</button>
        </form>
    </div>
</div>
